SELECT DISTINCT ?pic ?rockgroup ?rockgroupLabel ?country ?countryLabel
WHERE {
  SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search <<searchterm>>; #country
                      mwapi:language "en".
      ?country wikibase:apiOutputItem mwapi:item.
  }
  ?rockgroup wdt:P31 wd:Q5741069.
  ?rockgroup wdt:P495 ?country .
  ?rockgroup wdt:P136 ?band .
  OPTIONAL {?rockgroup wdt:P18 ?pic ; }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
  FILTER regex(str(?pic), "jpg").
}
ORDER BY ?rockgroup
LIMIT 5

SELECT ?person ?personLabel ?birthDate ?birthPlaceLabel WHERE {
  ?person wdt:P106 wd:Q639669 ; # occupation: jazz musician
         wdt:P19 ?birthPlace . # place of birth
  ?birthPlace wdt:P131* wd:Q1891 . # located in the administrative territorial entity (or its subentities) of: Bologna
  OPTIONAL { ?person wdt:P569 ?birthDate } # date of birth
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY ?personLabel

SELECT ?item ?itemLabel ?age WHERE {
  ?item wdt:P106 wd:Q639669; # occupation: jazz musician
        wdt:P19 wd:Q1891; # place of birth: Bologna
        wdt:P569 ?birth. # date of birth
  BIND(YEAR(NOW()) - YEAR(?birth) AS ?age) # calculate current age
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY ?age
LIMIT 30

SELECT ?item ?itemLabel ?death WHERE {
  ?item wdt:P106 wd:Q639669; # occupation: jazz musician
        wdt:P19 wd:Q1891; # place of birth: Bologna
        wdt:P570 ?death. # date of death
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC(?death)

