PREFIX wd: <http://www.wikidata.org/entity/>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
SELECT DISTINCT (year(?birthDate) as ?birthYear) ?birthPlace (year(?deathDate) as ?deathYear)  ?deathPlace ?pic 
WHERE
 
{
 
SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql>
 
{
    
    wd:Q9726 wdt:P18 ?pic .
 
 
OPTIONAL { wd:Q9726 wdt:P569 ?birthDate . }
 
    OPTIONAL { wd:Q9726 wdt:P19 ?bplace . ?bplace rdfs:label ?birthPlace }
 
OPTIONAL { wd:Q9726 wdt:P570 ?deathDate . }
    
       OPTIONAL { wd:Q9726 wdt:P20 ?dplace. ?dplace rdfs:label ?deathPlace }

  }
FILTER (lang(?birthPlace) = 'en')
FILTER (lang(?deathPlace)= 'en')
}
OFFSET 3

PREFIX dbp:<http://dbpedia.org/property/>
PREFIX dbr:<http://dbpedia.org/resource/>
SELECT DISTINCT ?Works (YEAR(?time) as ?year)
WHERE {
SERVICE <https://dbpedia.org/sparql> {
    ?s dbp:composer dbr:Gioachino_Rossini ; rdfs:label ?Works ; dbp:premiereDate ?time .
    FILTER langMatches(lang(?Works),"en")
  }
 }
ORDER BY ASC (?year)

PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX dbp: <http://dbpedia.org/property/>
PREFIX dpr: <http://dbpedia.org/resource/>
PREFIX dbr: <http://dbpedia.org/resource/>
PREFIX dbo: <http://dbpedia.org/ontology/>
SELECT DISTINCT  ?label (COUNT(distinct ?city) as ?count) 
WHERE {
SERVICE <https://dbpedia.org/sparql> 
  {
 dbr:Gioachino_Rossini a dbo:Person ; 
      dbo:wikiPageWikiLink ?city.
{  ?city a dbo:City . }
UNION 
{  ?city a dbr:City . }
UNION
{  ?city a dbo:Comune . }
?city rdfs:label ?label .
   FILTER langMatches(lang(?label),"en")
}

}
GROUP BY ?label ?count 
ORDER BY ?label

PREFIX wd: <http://www.wikidata.org/entity/>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX bd: <http://www.bigdata.com/rdf#>
PREFIX wikibase: <http://wikiba.se/ontology#>
PREFIX mwapi: <https://www.mediawiki.org/ontology#API/>
PREFIX bds: <http://www.bigdata.com/rdf/search#>

 

SELECT DISTINCT  ?musician ?musicianLabel (year(?birthDate) as ?birthYear) (year(?deathDate) as ?deathYear) ?Image

 

WHERE {
  ?musician owl:sameAs ?wiki .
  ?musician <http://www.ontologydesignpatterns.org/ont/amr/name> ?musicianLabel .
  filter contains(?musicianLabel, <<searchterm>>).
  SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql> {
        OPTIONAL {?wiki wdt:P569 ?birthDate ; wdt:P570 ?deathDate .}
        OPTIONAL { ?wiki wdt:P18 ?Image . FILTER regex(str(?Image), "jpg") }
        SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
       }
}
LIMIT 1

