SELECT DISTINCT (year(xsd:dateTime(?date)) as ?label)  (COUNT(?label) AS ?count) 
WHERE {
{?film ?a wd:Q11424} 
  UNION
  {?film ?a wd:Q17013749}
  UNION
  {?film ?a wd:Q93204}
  UNION
  {?film ?a wd:Q842256}
  UNION
  {?film ?a wd:Q24862}
  UNION
  {?film ?a wd:Q1054574}
  UNION
  {?film ?a wd:Q202866}
  UNION
  {?film ?a wd:Q130232}
?film wdt:P674 wd:Q1339 . 
  ?film rdfs:label ?filmLabel .
  ?film wdt:P577 ?date
  FILTER(LANG(?filmLabel)="en") .
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en".
          ?date rdfs:label ?label. }
}
GROUP BY ?count ?date
ORDER BY ?label

