SELECT DISTINCT (year(?birthDate) as ?birthYear)?birthPlace (year(?deathDate) as ?deathYear) ?deathPlace ?occupation ?pic
WHERE
{
SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql>
{
wd:Q207532  wdt:P18 ?pic .
OPTIONAL { wd:Q207532  wdt:P569 ?birthDate . }
OPTIONAL { wd:Q207532  wdt:P19 ?bplace . ?bplace rdfs:label ?birthPlace . }
OPTIONAL { wd:Q207532  wdt:P570 ?deathDate . }
OPTIONAL { wd:Q207532  wdt:P20 ?dplace. ?dplace rdfs:label ?deathPlace . }
OPTIONAL { wd:Q207532  wdt:P106 ?occ . ?occ rdfs:label ?occupation .  }
  
FILTER(lang(?birthPlace) = "en")
FILTER(lang(?deathPlace) = "en")
FILTER(lang(?occupation)= "en")
FILTER (regex (?occupation, "violinist"))
}
}

