SELECT DISTINCT ?composer  (year(?birthDate) as ?birthYear)?birthPlace (year(?deathDate) as ?deathYear) ?deathPlace ?pic 
WHERE {
  SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql> {
       FILTER CONTAINS (?birthPlace, <<searchterm>>). 
 	?person wdt:P136 wd:Q9730 ; #<has genre> <classical music>
            rdfs:label ?composer ;
          wdt:P19 ?bplace. 
          ?bplace rdfs:label ?birthPlace .

OPTIONAL { ?person  wdt:P569 ?birthDate . }
OPTIONAL { ?person  wdt:P570 ?deathDate . }
OPTIONAL { ?person  wdt:P20 ?dplace. ?dplace rdfs:label ?deathPlace . }
  OPTIONAL { ?person wdt:P18 ?pic . }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  FILTER regex(str(?pic), "jpg").
  FILTER (lang(?composer) = "en")
  FILTER (lang(?birthPlace) = "en")
 FILTER (lang(?deathPlace) = "en")
FILTER (YEAR(?birthDate) > 1799 && YEAR(?birthDate) < 1901 )
} }
LIMIT 10

